                const callback1 = (pcf) => {
                    if (pcf) {
                        this.#pcfList[pcfName] = pcf;
                        pcf.repository = repository;
                    }
                    resolve(pcf);
                };
                this.#loadPcf(repository, pcfName).then(callback1);
