class Kv3Value {
    isKv3Value = true;
    #type;
    #subType;
    #value;
    constructor(type, value, subType) {
        this.#type = type;
        this.#value = value;
        this.#subType = subType ?? Kv3Type.Unknown;
    }
    getType() {
        return this.#type;
    }
    getSubType() {
        return this.#subType;
    }
    isBoolean() {
        switch (this.#type) {
            case Kv3Type.Bool:
            case Kv3Type.True:
            case Kv3Type.False:
                return true;
        }
        return false;
    }
    isNumber() {
        switch (this.#type) {
            case Kv3Type.Int32:
            case Kv3Type.UnsignedInt32:
            case Kv3Type.IntZero:
            case Kv3Type.IntOne:
            case Kv3Type.Float:
            case Kv3Type.Double:
            case Kv3Type.DoubleZero:
            case Kv3Type.DoubleOne:
                return true;
        }
        return false;
    }
    isBigint() {
        switch (this.#type) {
            case Kv3Type.Int64:
            case Kv3Type.UnsignedInt64:
                return true;
        }
        return false;
    }
    isNumberArray() {
        switch (this.#subType) {
            case Kv3Type.Int32:
            case Kv3Type.UnsignedInt32:
            case Kv3Type.IntZero:
            case Kv3Type.IntOne:
            case Kv3Type.DoubleZero:
            case Kv3Type.DoubleOne:
            case Kv3Type.Float:
            case Kv3Type.Double:
                return this.isArray();
        }
        return false;
    }
    isBigintArray() {
        switch (this.#subType) {
            case Kv3Type.Int64:
            case Kv3Type.UnsignedInt64:
                return this.isArray();
        }
        return false;
    }
    isArray() {
        switch (this.#type) {
            case Kv3Type.Array:
            case Kv3Type.TypedArray:
            case Kv3Type.TypedArray2:
            case Kv3Type.TypedArray3:
                return true;
        }
        return false;
    }
    isVector() {
        switch (this.#subType) {
            case Kv3Type.TypedArray:
            case Kv3Type.TypedArray2:
            case Kv3Type.TypedArray3:
                return true;
        }
        return false;
    }
    getValue() {
        return this.#value;
    }
    exportAsText(linePrefix) {
        linePrefix = linePrefix ?? '';
        switch (this.#type) {
            case 6:
                return '"' + this.#value + '"';
            case Kv3Type.TypedArray:
            case Kv3Type.TypedArray2:
            case Kv3Type.TypedArray3:
                let typedArrayString = '';
                const linePrefix2 = linePrefix + '\t';
                for (const value of this.#value) {
                    switch (this.#subType) {
                        case Kv3Type.Resource:
                            typedArrayString += linePrefix2 + 'resource_name:"' + value + '",\n';
                            break;
                        case Kv3Type.Subclass:
                            typedArrayString += linePrefix2 + 'subclass:"' + value + '",\n';
                            break;
                        default:
                            typedArrayString += linePrefix2 + value + ',\n';
                            break;
                    }
                }
                return `\n${linePrefix}[\n${typedArrayString}${linePrefix}]\n`;
            case Kv3Type.Resource:
                return 'resource_name:"' + this.#value + '"';
            case Kv3Type.Subclass:
                return 'subclass:' + this.#value.exportAsText(linePrefix) + '';
        }
        return String(this.#value);
    }
}

